//   const cssClasses = `${classes.notification} ${specialClasses}`;

  return (
    <>
        {props.status === 'error' (<section className='notification'>
            <h2>{props.title}</h2>
            <p>{props.message}</p>
        </section>)}
    </>
  );
};

export default Notification;
